  const fields = [
    'Company Website',
    'Contact Person',
    'Speciality',
    'Country-Location',
    'Target Audience',
    'Company Info',
    'Comment',
    'Manager'
  ];

  // Инициализируем объект, где каждому полю соответствует пустая строка по умолчанию.
