  const item = items[itemIndex]; // Текущий обрабатываемый элемент
  console.log(`Обработка элемента ${itemIndex + 1} из ${items.length}`);

  // 2. Проверяем, содержит ли ТЕКУЩИЙ элемент нужные данные
  if (!item || !item.json || typeof item.json.text === 'undefined') {
    console.warn(`Элемент ${itemIndex + 1} пропущен: данные отсутствуют или имеют неверный формат (ожидается item.json.text).`);
    // Пропускаем этот элемент и переходим к следующему
    continue;
  }

  // 3. Получаем текст для ТЕКУЩЕГО элемента и убираем пробелы
  const inputText = String(item.json.text || "").trim();

  if (!inputText) {
    console.warn(`Элемент ${itemIndex + 1} пропущен: текст пуст после очистки пробелов.`);
    continue; // Переходим к следующему элементу
  }
  // console.log(`Текст элемента ${itemIndex + 1} (фрагмент):`, inputText.substring(0, 200));

  // 4. Обрабатываем текст ТЕКУЩЕГО элемента
  let formattedTime = null;
  let textContent = inputText; // По умолчанию текст - это весь inputText для этого элемента

  // Ищем время ЧЧ:ММ в конце текста ТЕКУЩЕГО элемента
  const timeRegex = /\n\s*(\d{1,2}:\d{2})\s*$/;
  const timeMatch = inputText.match(timeRegex);

  if (timeMatch) {
    // Если время найдено в конце текста этого элемента:
    formattedTime = timeMatch[1]; // Извлекаем время ЧЧ:ММ
    const timeStartIndex = inputText.lastIndexOf(timeMatch[0]); // Находим начало "\n...ЧЧ:ММ"
    // Текст - это всё ДО этой строки времени
    textContent = inputText.substring(0, timeStartIndex).trim();
    // console.log(`Элемент ${itemIndex + 1}: Найдено время '${formattedTime}'.`);
  } else {
    // Если время в конце текста этого элемента не найдено
    // console.warn(`Элемент ${itemIndex + 1}: Время в конце текста не найдено.`);
  }

  // 5. Создаем выходной объект для ТЕКУЩЕГО элемента
  //    Важно: создаем новый объект, чтобы не изменять исходный item напрямую
  const outputItem = {
    json: {
      // Сначала копируем все существующие поля из json исходного элемента (если нужно)
      // ...item.json,
