// Берём исходный текст и метаданные из первого элемента
const input = items[0].json;
const fullText = input.choices[0].message.content;
const { title, url, publishedAt, videoId } = input;

// Максимальная длина чанка
const maxLen = 3000;

// Разбиваем на массив предложений (учитываем точки, воскл. и вопросит. знаки)
const sentences = fullText.match(/[^\.!\?]+[\.!\?]+/g) || [fullText];

// Накопитель
let chunks = [];
let current = '';

// Проходим по каждому предложению
for (const sentence of sentences) {
  if ((current + sentence).length <= maxLen) {
    current += sentence + ' ';
  } else {
    if (current) chunks.push(current.trim());
    current = sentence + ' ';
  }
}
// Последний чанк
if (current) chunks.push(current.trim());

// Преобразуем в items, добавляя метаданные только в первый
return chunks.map((textChunk, index) => {
  const base = { chunk: textChunk };
  if (index === 0) {
    // добавляем поля только для первого элемента
    return {
      json: {
        ...base,
        title,
        url,
        publishedAt,
        videoId,
      },
    };
  }
  // последующие — только chunk
  return { json: base };
});

// Инициализируем пустую строку для сбора текста из всех входных айтемов
let allFullText = '';

// Переменные для метаданных, берем их из первого айтема
let title, url, publishedAt, videoId;

// Берем метаданные только из первого входного айтема, если он существует
if (items.length > 0 && items[0].json) {
  const firstItemJson = items[0].json;
  title = firstItemJson.title;
  url = firstItemJson.url;
  publishedAt = firstItemJson.publishedAt;
  videoId = firstItemJson.videoId;
}


// Проходим по всем входным айтемам и собираем текст
for (const item of items) {
  // Проверяем, существует ли поле с текстом в текущем айтеме
  if (item.json && item.json.choices && item.json.choices[0] && item.json.choices[0].message && item.json.choices[0].message.content) {
    // Добавляем текст текущего айтема к общей строке.
    // Добавляем перевод строки между текстами разных айтемов для лучшего разделения.
    allFullText += item.json.choices[0].message.content + '\n';
  }
}

// Максимальная длина чанка (остается прежней)
const maxLen = 3000;

// Разбиваем объединенный текст на предложения
// Учитываем случай, если allFullText пустой
const sentences = allFullText.match(/[^\.!\?]+[\.!\?]+/g) || (allFullText ? [allFullText] : []);


// Собираем чанки из объединенных предложений (логика остается прежней)
let chunks = [];
let current = '';
for (const sentence of sentences) {
  // Убираем лишние пробелы из начала/конца предложения перед проверкой длины
  const trimmedSentence = sentence.trim();
  if (!trimmedSentence) continue; // Пропускаем пустые строки после trim

  // Проверяем, поместится ли предложение в текущий чанк
  // Учитываем пробел, который добавим после предложения
  if ((current + trimmedSentence).length + (current ? 1 : 0) <= maxLen) {
    current += trimmedSentence + ' ';
  } else {
    // Если текущий чанк не пустой, добавляем его в список чанков
    if (current) chunks.push(current.trim());
    // Начинаем новый чанк с текущего предложения
    current = trimmedSentence + ' ';
  }
}
// Добавляем последний чанк, если он не пустой
if (current) chunks.push(current.trim());


// Преобразуем чанки в выходные айтемы.
// Метаданные добавляем только к первому чанку общего текста.
return chunks.map((text, idx) => {
  const base = { summary_points: text };
  if (idx === 0) {
    // Только в первом выходном айтеме (первом чанке) добавляем метаданные из первого входного айтема
    return {
      json: {
        ...base,
        title, // Метаданные из первого входного айтема
        url,
        publishedAt,
        videoId,
      }
    };
  }
  // Для последующих чанков возвращаем только текст
  return { json: base };
});

// Node 1: Clean the raw string containing the JSON array

// Инициализируем переменную для сбора всех частей сырой JSON строки из входных айтемов
let combinedRawJsonString = '';

// Переменные для метаданных, берем их из первого входного айтема
let title, url;

// Берем метаданные (title, url) только из первого входного айтема, если он существует
if (items.length > 0 && items[0].json) {
    const firstInputItemJson = items[0].json;
    title = firstInputItemJson.title;
    url = firstInputItemJson.url;
    // publishedAt и videoId также доступны, но в оригинале не использовались в выходе этого узла.
}

// Проходим по всем входным айтемам и собираем содержимое поля summary_points
for (const item of items) {
    // Проверяем, существует ли поле summary_points в текущем айтеме
    if (item.json && item.json.summary_points) {
        // Добавляем содержимое summary_points текущего айтема к общей строке
        // Просто конкатенируем, т.к. чистка ниже должна справиться с возможными разрывами
        combinedRawJsonString += item.json.summary_points;
    }
}

// Теперь работаем с объединенной сырой строкой
let cleanedJsonString = combinedRawJsonString;

// --- Cleaning Steps ---

// 1. Remove potential leading 'json' marker and surrounding whitespace/newlines.
cleanedJsonString = cleanedJsonString.replace(/^\s*```?\s*json\s*\n*/, '');

// 2. Remove potential trailing backticks (``` or `) and surrounding whitespace/newlines.
cleanedJsonString = cleanedJsonString.replace(/\s*```?\s*$/, '');

// 3. Trim any remaining leading/trailing whitespace.
cleanedJsonString = cleanedJsonString.trim();

// --- !!! WARNING !!! ---
// The following step removes JSON structure characters ([ ] { }).
// As noted in the original code, this makes the string invalid JSON and JSON.parse() in the next node WILL FAIL
// if the next node expects valid JSON. This logic is kept as per your original code.

// 4. Remove square brackets ([ ]) and curly braces ({ }) using a regex
cleanedJsonString = cleanedJsonString.replace(/[\[\]\{\}]/g, '');

// --- Prepare Output for the next node ---
// Мы возвращаем один айтем, содержащий очищенную объединенную строку и метаданные из первого входного айтема.
return [{
    json: {
        cleanedJsonString: cleanedJsonString, // Очищенная объединенная строка
        title: title,                         // Оригинальный title из первого входного айтема
        url: url                              // Оригинальный URL из первого входного айтема
    }
}];

// Получаем очищенную строку из предыдущего узла
const cleanedJsonString = items[0].json.cleanedJsonString;

// Шаблон для парсинга — регулярное выражение для извлечения topic и detail
const regex = /"topic":\s*"([^"\\]*(\\.[^"\\]*)*)"\s*,\s*"detail":\s*"([^"\\]*(\\.[^"\\]*)*)"/gs;

// Массив для хранения результата
let result = [];
let match;

// Извлекаем метаданные из первого элемента
const title = items[0].json.title;
const url = items[0].json.url;

// Проходим по строкам и извлекаем значения для topic и description
let isFirstRow = true; // Переменная для отслеживания первой строки
while ((match = regex.exec(cleanedJsonString)) !== null) {
    // match[1] содержит значение 'topic'
    // match[3] содержит значение 'detail'
    let row = {
        json: {
            Topic: match[1],       // Тема
            Description: match[3]  // Описание
        }
    };

    // Добавляем title и url только для первой строки
    if (isFirstRow) {
        row.json.title = title;
        row.json.url = url;
        isFirstRow = false; // После первой строки устанавливаем флаг в false
    }

    result.push(row);
}

// Возвращаем результат в формате, где каждая строка представляет собой объект с Topic, Description, title и url (для первой строки)
return result;
