// Получаем HTML-текст из предыдущей ноды
let description = $input.first().json.description;

// Массив для хранения результатов с URL и датой
let records = [];

// Обновлённое регулярное выражение, которое ищет блок "Company website:" и затем либо тег <a href="...">, либо прямую ссылку (http/https)
const regex = /<b>\s*Company website:\s*<\/b>\s*(?:<a\s+href="([^"]+)"|((?:https?:\/\/)[^\s<]+))/gi;

// Регулярное выражение для поиска даты в формате "YYYY-MM-DD"
// Если у вас формат даты другой, замените регулярное выражение на подходящий
const dateRegex = /(\d{4}-\d{2}-\d{2})/;

let match;
while ((match = regex.exec(description)) !== null) {
  // Если группа 1 существует, значит ссылка взята из тега <a>, иначе из группы 2 — из plain-текста
  let href = match[1] || match[2];
  let outputUrl = href;

  // Если ссылка проходит через редирект Google, извлекаем значение параметра q
  const googleRegex = /https:\/\/www\.google\.com\/url\?q=([^&]+)/i;
  const googleMatch = href.match(googleRegex);
  if (googleMatch) {
    outputUrl = googleMatch[1];
  }

  // Извлекаем дату из текста, прилегающего к найденному блоку.
  // Здесь берём 200 символов начиная с позиции совпадения.
  let context = description.slice(match.index, match.index + 200);
  let dateMatch = dateRegex.exec(context);
  let dateObj = dateMatch ? new Date(dateMatch[1]) : null;

  // Добавляем объект с URL и датой в массив результатов
  records.push({ url: outputUrl, date: dateObj });
}

// Если записей не найдено, возвращаем объект с сообщением (чтобы всегда возвращались данные)
if (records.length === 0) {
  return [ { json: { message: "No matching records found" } } ];
}

let now = new Date();

// Фильтруем записи, у которых есть корректная дата
let recordsWithDate = records.filter(r => r.date !== null);

// Если ни у одной записи нет даты, возвращаем первую найденную запись
if (recordsWithDate.length === 0) {
  return [ { json: records[0] } ];
}

// Находим запись, дата которой по абсолютной разнице с текущей датой минимальна
let closestRecord = recordsWithDate.reduce((prev, curr) => {
  let prevDiff = Math.abs(now - prev.date);
  let currDiff = Math.abs(now - curr.date);
  return currDiff < prevDiff ? curr : prev;
});

// Возвращаем результат, обернутый в объект json
return [ { json: closestRecord } ];

