// Функция для округления времени до ближайшего полчасового интервала (с использованием UTC)
// Если минуты меньше 30, округляем до :30, иначе до начала следующего часа
function getHalfHourBlockUTC(dateString) {
  const dateObj = new Date(dateString);
  if (isNaN(dateObj.getTime())) return "";
  let hours = dateObj.getUTCHours();
  const minutes = dateObj.getUTCMinutes();

  if (minutes < 30) {
    return `${String(hours).padStart(2, '0')}:30`;
  } else {
    hours = hours + 1;
    if (hours === 24) {
      hours = 0;
    }
    return `${String(hours).padStart(2, '0')}:00`;
  }
}

// Этап 1. Получаем все входящие элементы и проводим обработку полей summary, description и end
const items = $input.all();

// Проверяем, что items – массив
if (!Array.isArray(items)) {
  throw new Error("Ожидался массив входящих элементов");
}

const tempResults = items.map(item => {
  // Если структура не соответствует ожиданиям, пропускаем элемент
  if (!item || !item.json) return null;

  const jsonData = item.json;

  // Извлекаем поля summary и description, остальные поля сохраняем в restFields
  const summary = jsonData.summary || "";
  const description = jsonData.description || "Нет описания";

  // Очищаем поле summary: удаляем подстроку "- Remote Helpers" (без учета регистра)
  let cleanedSummary = summary.replace(/-\s*Remote\s*Helpers\s*/gi, "").trim();

  // Получаем оригинальное время окончания из jsonData.end
  let originalEnd = "";
  if (jsonData.end) {
    originalEnd = jsonData.end.dateTime || jsonData.end.date || "";
  }

  // Форматируем время окончания в формате HH:MM (округление до ближайшего полчасового интервала)
  const formattedTime = originalEnd ? getHalfHourBlockUTC(originalEnd) : "";

  // Формируем итоговый объект.
  // Сохраняем оригинальное время окончания (_originalEnd) для фильтрации и сортировки.
  const finalData = {
    ...jsonData,
    summary: cleanedSummary,
    description: description,
    formattedTime: formattedTime,  // переименованное значение end
    _originalEnd: originalEnd
  };

  return { json: finalData };
})
// Фильтруем элементы, у которых не удалось сформировать объект
.filter(item => item !== null);

// Этап 2. Фильтрация дубликатов с использованием комбинации summary и округленного времени (UTC)
// Используем _originalEnd для определения уникальности
const seenKeys = new Set();
const uniqueResults = tempResults.filter(item => {
  const currentSummary = item.json.summary;
  const originalEndTime = item.json._originalEnd;

  // Если время окончания задано, округляем его по UTC, иначе оставляем пустую строку
  const normalizedTime = originalEndTime ? getHalfHourBlockUTC(originalEndTime) : "";

  // Если у события есть уникальный id, используем его; иначе – комбинацию summary и нормализованного времени
  const uniqueId = item.json.id;
  const dedupKey = uniqueId ? uniqueId : `${currentSummary}__${normalizedTime}`;

  if (seenKeys.has(dedupKey)) {
    return false;
  } else {
    seenKeys.add(dedupKey);
    return true;
  }
});

// Этап 3. Сортировка по оригинальному времени окончания (используя _originalEnd)
uniqueResults.sort((a, b) => {
  const timeA = new Date(a.json._originalEnd);
  const timeB = new Date(b.json._originalEnd);
  return timeA - timeB;
});

// Удаляем вспомогательное поле _originalEnd из финального вывода
const finalResults = uniqueResults.map(item => {
  delete item.json._originalEnd;
  return item;
});

// Возвращаем итоговый массив уникальных, отсортированных по времени окончания
return finalResults;

