function cleanText(inputText) {
  if (typeof inputText !== 'string') {
    // В реальной среде автоматизации лучше выбрасывать ошибку
    // throw new Error("Input must be a string.");
    console.error("Input must be a string.");
    return ''; // Возвращаем пустую строку в случае ошибки типа
  }
  const thinkTagRegex = /<think>.*?<\/think>/gs;
  let cleanedText = inputText.replace(thinkTagRegex, '');
  const bracketNumberRegex = /\[\d+\]/g;
  cleanedText = cleanedText.replace(bracketNumberRegex, '');
  return cleanedText;
}

/**
 * Cleans the input text and structures the result as an array of objects,
 * where each object represents a non-empty line from the cleaned text.
 * (Без изменений)
 * @param {string} inputText The original string with think tags and bracketed numbers.
 * @returns {Array<object>} An array of objects, e.g., [{ id: 0, lineContent: "..." }, ...].
 */
function cleanAndStructureOutput(inputText) {
  const cleanedFullText = cleanText(inputText);
  const lines = cleanedFullText.split('\n');
  const outputArray = [];
  let idCounter = 0;
  for (const line of lines) {
    const trimmedLine = line.trim();
    if (trimmedLine !== '') {
      outputArray.push({
        id: idCounter++,
        lineContent: trimmedLine
      });
    }
  }
  return outputArray;
}

const text = $input.first().json.data; // Или $input[0].json.data в стандартном JS
const result = cleanAndStructureOutput(text);
return result;

const items = $input.all();
const joinedText = items.map(item => item.json.lineContent).join('\n');
return [{ json: { joinedText } }];

// Получаем значение id из первого элемента входящих данных
const id = $input.first().json.id;

// Формируем ссылку на Google Документ, подставляя id
const googleDocLink = `https://docs.google.com/document/d/${id}`;

// Возвращаем объект с новым полем, содержащим ссылку на документ
return [
  {
    json: {
      id: id,
      googleDocLink: googleDocLink,
    }
  }
];

for (const item of $input.all()) {
  // Получаем контент из входящих данных
  const content = item.json.content || "";

  // Используем регулярное выражение для поиска текста после "**company name**:"
  // - \*\*company name\*\* ищет буквальное "**company name**:" (без учёта регистра благодаря флагу i)
  // - \s* — допускает наличие пробелов после двоеточия
  // - ([^\n]+) — захватывает все символы до первого переноса строки, то есть само название компании
  const match = content.match(/\*\*company name\*\*:\s*([^\n]+)/i);
  const companyName = match ? match[1].trim() : "Not found";

  // Превращаем documentId в ссылку на Google Документ
  const docId = item.json.documentId || "";
  const googleDocLink = docId ? `https://docs.google.com/document/d/${docId}` : "";

  // Формируем поля для вывода:
  // - companyName: извлечённое название компании
  // - fullText: исходный текст
  // - googleDocLink: полноценная ссылка на Google Документ
  item.json.companyName = companyName;
  item.json.fullText = content;
  item.json.googleDocLink = googleDocLink;
}

return $input.all();

// Получаем значение id из первого элемента входящих данных
const id = $input.first().json.id;

// Формируем ссылку на Google Документ, подставляя id
const googleDocLink = `https://docs.google.com/document/d/${id}`;

// Возвращаем объект с новым полем, содержащим ссылку на документ
return [
  {
    json: {
      id: id,
      googleDocLink: googleDocLink,
    }
  }
];

// Loop over input items
for (const item of $input.all()) {
  // Достаём documentId и формируем полную ссылку
  const docId = item.json.documentId;
  const googleDocLink = `https://docs.google.com/document/d/${docId}`;

  // Достаём content и ищем в нём Company Name
  const content = item.json.content || '';
  // С помощью RegExp ищем строку: "Company Name: ...."
  const companyNameMatch = content.match(/Company name: \s*(.*)/i);
  const companyName = companyNameMatch ? companyNameMatch[1].trim() : 'Not found';

  // Добавляем новые поля к исходным данным
  item.json.googleDocLink = googleDocLink;
  item.json.companyName = companyName;
}

return $input.all();

// Обрабатываем все входящие элементы
for (const item of $input.all()) {
  // Извлекаем текст
  const text = item.json.text || '';

  // С помощью RegExp ищем строку: "Company Name: ...."
  const companyNameMatch = text.match(/Company Name:\s*(.*)/i);
  const companyName = companyNameMatch ? companyNameMatch[1].trim() : 'Not found';

  // Добавляем в текущий элемент два новых поля:
  // 1) companyName — название компании
  // 2) fullText — исходный текст
  item.json.companyName = companyName;
  item.json.fullText = text;
}

// Возвращаем все обработанные элементы
return $input.all();

// Получаем текст из входящего JSON
const text = $input.first().json.joinedText;

// Регулярное выражение для извлечения названия компании
const regex = /^\*\*company name\*\*: ([^\n]+)\n/;
const match = text.match(regex);
const companyName = match ? match[1].trim() : "";

// Возвращаем объект с двумя полями: название компании и полный текст
return { json: { companyName, fullText: text } };
